/**
 * Creates an example PDF TEST document using TCPDF
 * @package com.tecnick.tcpdf
 * @abstract TCPDF - Example: HTML justification
 * @author Nicola Asuni
 * @since 2008-10-18
 */

// Include the main TCPDF library (search for installation path).
require_once('tcpdf_include.php');

// create new PDF document
$pdf = new TCPDF(PDF_PAGE_ORIENTATION, PDF_UNIT, PDF_PAGE_FORMAT, true, 'UTF-8', false);

// set document information

// set auto page breaks


// set image scale factor
$pdf->setImageScale(PDF_IMAGE_SCALE_RATIO);

// set some language-dependent strings (optional)


// ---------------------------------------------------------

// add a page
$pdf->AddPage();

$html =   "

<!DOCTYPE html>
<html>

<head>
  <title>HTML to API - event tickets</title>
  <link href='https://fonts.googleapis.com/css?family=Kreon:700|Audiowide&subset=latin,latin-ext' rel='stylesheet' type='text/css'>
  <!-- <link rel=\"stylesheet\" href=\"styles/main.css\" media=\"screen\" charset=\"utf-8\"/> -->
  <meta content=\"width=device-width, initial-scale=1.0\" name=\"viewport\">
  <meta http-equiv=\"content-type\" content=\"text-html; charset=utf-8\">
</head>

<style>
  body {
    margin: 0;
    color: #872F67;
    font-family: \"Audiowide\", cursive;
    font-weight: 400;
    font-size: 25px;
  }

  .container {
    width: 795px;
    margin: 0 auto;
  }

  section {
    position: relative;
    height: 280px;
    width: 100%;
    background-image: url(data:image/svg+xml;charset=utf-8;base64,);
    background-repeat: no-repeat;
    overflow: hidden;
  }
  section .left {
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    float: left;
    width: 685px;
    padding: 35px 0 0 110px;
    text-align: center;
  }
  section .right {
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    float: right;
    width: 110px;
    padding: 30px 30px 0 0;
  }
  section .event {
    margin-bottom: 30px;
    font-weight: 700;
    font-size: 1.6em;
    line-height: 35px;
    text-transform: uppercase;
  }
  section .title {
    margin-bottom: 20px;
    color: #4AB4C4;
    font-size: 4em;
    line-height: 72px;
  }
  section .info {
    font-size: 0.8em;
    text-transform: uppercase;
  }
  section .seats {
    margin-bottom: 15px;
    color: #4AB4C4;
    font-family: \"Kreon\", serif;
    font-size: 0.36em;
    text-align: center;
    text-transform: uppercase;
  }
  section .seats:last-child {
    margin-bottom: 0;
  }
  section .seats span {
    display: block;
    margin-bottom: 5px;
    padding: 10px 0;
    color: #fff;
    background: #4AB4C4;
    font-size: 2.777em;
  }
</style>

<body>
  <div class=\"container\">
    <section>
      <div class=\"left\">
        <div class=\"event\">Live in concert</div>
        <div class=\"title\">Joe Doe</div>
        <div class=\"info\">Saturday, August 25 2020,<br>The Plaza, New York</div>
      </div>
      <div class=\"right\">
        <div class=\"seats\"><span>A</span>section</div>
        <div class=\"seats\"><span>13</span>row</div>
        <div class=\"seats\"><span>120</span>seat</div>
      </div>
    </section>
  </div>
</body>

</html>

";
// set core font
$pdf->SetFont('helvetica', '', 10);

// output the HTML content
$pdf->writeHTML($html, true, false, false, false,'');

$pdf->Ln();

// set UTF-8 Unicode font
$pdf->SetFont('dejavusans', '', 10);

// output the HTML content
$pdf->writeHTML($html, true, 0, true, true);

// reset pointer to the last page
$pdf->lastPage();

// ---------------------------------------------------------

//Close and output PDF document
$pdf->Output('example_039.pdf', 'I');

